class Solution(object):
    def maximalRectangle(self, matrix):
        """
        :type matrix: List[List[str]]
        :rtype: int
        """
        if len(matrix) == 0 or matrix == [[]]:
            return 0
        self.matrix = matrix
        return self.dp(0, len(matrix), 0, len(matrix[0]))
    
    def dp(self, row1, row2, col1, col2):
        # get the maximal rectangle area for part of the matrix
        # from row1:row2 and col1:col2
        if row2 == row1 or col2 == col1:
            return 0
        if row2 == row1+1 and col2 == col1+1:
            return int(self.matrix[row1][col1]=='1')
        for row in range(row1, row2):
            for col in range(col1, col2):
                if self.matrix[row][col] == '0':
                    max_above = self.dp(row1, row, col1, col2)
                    max_below = self.dp(row+1, row2, col1, col2)
                    max_left = self.dp(row1, row2, col1, col)
                    max_right = self.dp(row1, row2, col+1, col2)
                    return max(max_above, max_below, max_left, max_right)
        else:
            return (row2-row1)*(col2-col1)

class Solution(object):
    def maximalRectangle(self, matrix):
        """
        :type matrix: List[List[str]]
        :rtype: int
        """
        if len(matrix) == 0 or matrix == [[]]:
            return 0
        n = len(matrix[0])
        height = [0]*(n+1)
        #height[n] = len(matrix)+1
        res = 0
        
        for row in matrix:
            for i in range(n):
                height[i] = height[i] + 1 if row[i] == '1' else 0
            stack = []
            #print(height)
            for i_pos, h in enumerate(height):
                current_pos = i_pos
                while stack and h < stack[-1][0]:
                    current_h, current_pos = stack.pop()
                    res = max(res, current_h*(i_pos-current_pos))
                    #print(res)
                stack.append([h, current_pos])
        
        return res


